"use client";
import * as React from "react";
import * as HoverCardPrimitive from "@radix-ui/react-hover-card";

import { cn } from "@/lib/utils";

interface HoverCardProps {
  children: React.ReactNode | string;
  content?: React.ReactNode;
  align?: "start" | "center" | "end";
  sideOffset?: number;
  className?: string;
  open?: any;
  onOpenChange?: any;
}

const HoverCard: React.FC<HoverCardProps> = ({
  children,
  content,
  className,
  align = "center",
  sideOffset = 4,
  ...props
}) => {
  const wrappedChildren =
    typeof children === "string" || typeof children === "number" ? (
      <>{children}</>
    ) : (
      children
    );

  return (
    <HoverCardPrimitive.Root {...props}>
      {content ? (
        <>
          <HoverCardPrimitive.Trigger asChild>
            {wrappedChildren}
          </HoverCardPrimitive.Trigger>
          <HoverCardPrimitive.Content
            align={align}
            sideOffset={sideOffset}
            className={cn(
              "z-50 w-64 rounded-md border bg-background p-4 text-foreground shadow-md outline-none data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2",
              className,
            )}
          >
            {content}
          </HoverCardPrimitive.Content>
        </>
      ) : (
        wrappedChildren
      )}
    </HoverCardPrimitive.Root>
  );
};

const HoverCardTrigger = HoverCardPrimitive.Trigger;

const HoverCardContent = React.forwardRef<
  React.ElementRef<typeof HoverCardPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof HoverCardPrimitive.Content>
>(({ className, align = "center", sideOffset = 4, ...props }, ref) => (
  <HoverCardPrimitive.Content
    ref={ref}
    align={align}
    sideOffset={sideOffset}
    className={cn(
      "z-50 w-64 rounded-md border bg-background p-4 text-foreground shadow-md outline-none data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2",
      className,
    )}
    {...props}
  />
));
HoverCardContent.displayName = HoverCardPrimitive.Content.displayName;

export { HoverCard, HoverCardTrigger, HoverCardContent };
