    code: [{"title":"form.tsx","code":"\"use client\";\r\n\r\nimport { useRef, useState } from \"react\";\r\nimport Link from \"next/link\";\r\nimport { zodResolver } from \"@hookform/resolvers/zod\";\r\nimport { useForm } from \"react-hook-form\";\r\nimport { z } from \"zod\";\r\n\r\nimport { Button } from \"@/lib/components/core/default/react/buttons/button\";\r\nimport {\r\n  Form,\r\n  FormControl,\r\n  FormDescription,\r\n  FormField,\r\n  FormItem,\r\n  FormLabel,\r\n  FormMessage,\r\n} from \"@/lib/components/core/default/react/form/form\";\r\nimport {\r\n  MultiSelect,\r\n  MultiSelectContent,\r\n  MultiSelectEmpty,\r\n  MultiSelectList,\r\n  type MultiSelectOption,\r\n  type MultiSelectOptionGroup,\r\n  MultiSelectSearch,\r\n  MultiSelectTrigger,\r\n  MultiSelectValue,\r\n  renderMultiSelectOptions,\r\n} from \"@/lib/components/core/default/react/input/multi-select\";\r\n// import { toast } from '~/components/ui/use-toast'\r\n\r\nconst ALL_ITEMS = [\r\n  { value: \"react\", label: \"React\", group: \"React\" },\r\n  { value: \"next\", label: \"Next.js\", group: \"React\" },\r\n  { value: \"remix\", label: \"Remix\", group: \"React\" },\r\n  { value: \"vue\", label: \"Vue\", group: \"Vue\" },\r\n  { value: \"nuxt\", label: \"Nuxt.js\", group: \"Vue\" },\r\n  { value: \"angular\", label: \"Angular\", group: \"Others\" },\r\n  { value: \"svelte\", label: \"Svelte\", group: \"Others\" },\r\n];\r\n\r\nfunction group(\r\n  options: Array<(typeof ALL_ITEMS)[number]>,\r\n): MultiSelectOptionGroup[] {\r\n  return options.reduce((acc, item) => {\r\n    const group = acc.find((g) => g.heading === item.group);\r\n    if (group) group.children.push(item);\r\n    else acc.push({ heading: item.group, children: [item] });\r\n\r\n    return acc;\r\n  }, [] as MultiSelectOptionGroup[]);\r\n}\r\n\r\nasync function search(keyword?: string) {\r\n  if (!keyword) return group(ALL_ITEMS);\r\n  const lowerKeyword = keyword.toLowerCase();\r\n  const filtered = ALL_ITEMS.filter((item) =>\r\n    item.label.toLowerCase().includes(lowerKeyword),\r\n  );\r\n\r\n  if (!filtered.length) {\r\n    return [\r\n      {\r\n        label: keyword,\r\n        value: keyword,\r\n      },\r\n    ];\r\n  }\r\n\r\n  return group(filtered);\r\n}\r\n\r\nconst FormSchema = z.object({\r\n  frameworks: z.array(z.string(), {\r\n    required_error: \"Please select at least one framework.\",\r\n  }),\r\n});\r\n\r\nexport default function MultiSelectDemo() {\r\n  const form = useForm<z.infer<typeof FormSchema>>({\r\n    resolver: zodResolver(FormSchema),\r\n  });\r\n\r\n  const [loading, setLoading] = useState(false);\r\n  const [options, setOptions] = useState<MultiSelectOption[]>(() =>\r\n    group(ALL_ITEMS),\r\n  );\r\n\r\n  const indexRef = useRef(0);\r\n\r\n  const handleSearch = async (keyword: string) => {\r\n    const index = ++indexRef.current;\r\n    setLoading(true);\r\n    const newOptions = await search(keyword);\r\n    if (indexRef.current === index) {\r\n      setOptions(newOptions);\r\n      setLoading(false);\r\n    }\r\n  };\r\n\r\n  const onSubmit = (data: z.infer<typeof FormSchema>) => {\r\n    /*  toast({\r\n         title: 'You submitted the following values:',\r\n         description: (\r\n            <pre className=\"mt-2 w-[340px] rounded-md bg-slate-950 p-4\">\r\n               <code className=\"text-white\">{JSON.stringify(data, null, 2)}</code>\r\n            </pre>\r\n         ),\r\n      }) */\r\n  };\r\n\r\n  return (\r\n    <Form {...form}>\r\n      <form onSubmit={form.handleSubmit(onSubmit)} className=\"w-2/3 space-y-6\">\r\n        <FormField\r\n          control={form.control}\r\n          name=\"frameworks\"\r\n          render={({ field }) => (\r\n            <FormItem>\r\n              <FormLabel>Frameworks</FormLabel>\r\n              <MultiSelect\r\n                onValueChange={field.onChange}\r\n                defaultValue={field.value}\r\n                onSearch={(keyword) => handleSearch(keyword ?? \"\")}\r\n              >\r\n                <FormControl>\r\n                  <MultiSelectTrigger className=\"w-96\">\r\n                    <MultiSelectValue placeholder=\"Select frameworks\" />\r\n                  </MultiSelectTrigger>\r\n                </FormControl>\r\n                <MultiSelectContent>\r\n                  <MultiSelectSearch />\r\n                  <MultiSelectList>\r\n                    {loading ? null : renderMultiSelectOptions(options)}\r\n                    <MultiSelectEmpty>\r\n                      {loading ? \"Loading...\" : \"No results found\"}\r\n                    </MultiSelectEmpty>\r\n                  </MultiSelectList>\r\n                </MultiSelectContent>\r\n              </MultiSelect>\r\n              <FormDescription>\r\n                You can manage frameworks in{\" \"}\r\n                <Link href=\"/examples/forms\">framework settings</Link>.\r\n              </FormDescription>\r\n              <FormMessage />\r\n            </FormItem>\r\n          )}\r\n        />\r\n        <Button type=\"submit\">Submit</Button>\r\n      </form>\r\n    </Form>\r\n  );\r\n}\r\n"}]
