    code: [{"title":"default.tsx","code":"\"use client\";\r\n\r\nimport { Label } from \"@/lib/components/core/default/react/input/label\";\r\nimport { Button } from \"@/lib/components/core/default/react/buttons/button\";\r\nimport { TextField } from \"@/lib/components/core/default/react/input/text-field\";\r\nimport { Popover } from \"@/lib/components/core/default/react/overlay/popover\";\r\n\r\nexport default function PopoverDemo() {\r\n\r\n    const Content = () => (\r\n        <div className=\"grid gap-4\">\r\n           <div className=\"space-y-2\">\r\n              <h4 className=\"font-medium leading-none\">Dimensions</h4>\r\n              <p className=\"text-muted-foreground text-sm\">\r\n                 Set the dimensions for the layer.\r\n              </p>\r\n           </div>\r\n           <div className=\"grid gap-2\">\r\n              <div className=\"grid grid-cols-3 items-center gap-4\">\r\n                 <Label htmlFor=\"width\">Width</Label>\r\n                 <TextField\r\n                    id=\"width\"\r\n                    defaultValue=\"100%\"\r\n                    className=\"col-span-2 h-8\"\r\n                 />\r\n              </div>\r\n              <div className=\"grid grid-cols-3 items-center gap-4\">\r\n                 <Label htmlFor=\"maxWidth\">Max. width</Label>\r\n                 <TextField\r\n                    id=\"maxWidth\"\r\n                    defaultValue=\"300px\"\r\n                    className=\"col-span-2 h-8\"\r\n                 />\r\n              </div>\r\n              <div className=\"grid grid-cols-3 items-center gap-4\">\r\n                 <Label htmlFor=\"height\">Height</Label>\r\n                 <TextField\r\n                    id=\"height\"\r\n                    defaultValue=\"25px\"\r\n                    className=\"col-span-2 h-8\"\r\n                 />\r\n              </div>\r\n              <div className=\"grid grid-cols-3 items-center gap-4\">\r\n                 <Label htmlFor=\"maxHeight\">Max. height</Label>\r\n                 <TextField\r\n                    id=\"maxHeight\"\r\n                    defaultValue=\"none\"\r\n                    className=\"col-span-2 h-8\"\r\n                 />\r\n              </div>\r\n           </div>\r\n        </div>\r\n    );\r\n\r\n    return (\r\n        <Popover content={<Content />} className=\"w-80\" >\r\n            <Button>Open popover</Button>\r\n        </Popover>\r\n    )\r\n}\r\n"}]
